import '../src/App.css'
import Header from '../src/Header'
import Slider from './Slider';
import Find from './Find';
import About from './About';
import Sale from './SaleSection';
import Price from './PriceSection';
import Deal from './DealSection';
import '../src/Components/css/style.css'
import '../src/Components/css/responsive.css'
import '../src/Components/css/bootstrap.css'
import '../src/Components/css/style.css.map'
import SotrudUs from './SotrudSection';
import Client from './Client';
import Contact from './Contact';
import Info from './Information';
import Footer from './Footer';
import { useState } from 'react';
import Cart from './Cart';
import { Routes, Route, Link } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';

function App() {
  const [cart, setCart] = useState([])
  const [isCartOpen, setIsCartOpen] = useState(false)

  function toggleCart() {
    setIsCartOpen(!isCartOpen)
  }

  function addToCart(product) {
    const existingProduct = cart.find(item=> item.id===product.id);
    if(existingProduct) {
      setCart(cart.map(item=> 
        item.id === product.id ? {...existingProduct, quanity: existingProduct.quanity + 1} :item
        ));
    }else {
      setCart([...cart, {...product, quanity: 1}]);
    }
  }

  function deleteHouse(productid) {
    setCart(cart.filter(item=> item.id !== productid))
  }


  return (
    <div className="App">
      <div className='hero_area'>      
         <Header onclick = {toggleCart}/>
         <Slider/>
         <Find/>
         <About/>
         <Sale addToCart={addToCart}/>
         {isCartOpen && (<Cart cart={cart} deleteHouse={deleteHouse} onclose/>)}

         <Price/>
         <Deal/>
         <SotrudUs/>
         <Client/>
         <Contact/>
         <Info/>
         <Footer/>
      </div>


    </div>
  );
}

export default App;
