// Client.js
import React from 'react';
import Slider from 'react-slick';
import '../src/App.css'; // Убедитесь, что путь к файлу правильный
import client from '../src/Components/images/client.jpg';

const dataClient = [
  { img: client, zagolovok: 'Majority', podzagolovok: 'There are many variations of passages of Lorem Ipsum available, but the majority have suffered alteration in some form, by injected humour, or randomised words which don`t look even slightly believable.' },
  { img: client, zagolovok: 'Majority', podzagolovok: 'There are many variations of passages of Lorem Ipsum available, but the majority have suffered alteration in some form, by injected humour, or randomised words which don`t look even slightly believable.' },
  { img: client, zagolovok: 'Majority', podzagolovok: 'There are many variations of passages of Lorem Ipsum available, but the majority have suffered alteration in some form, by injected humour, or randomised words which don`t look even slightly believable.' }
];

export default function Client() {
  const settings = {
    dots: true,
    infinite: true,
    speed: 700,
    slidesToShow: 1,
    slidesToScroll: 1
  };

  return (
    <section className="client_section layout_padding">
      <div className="container-fluid">
        <div className="heading_container">
          <h2>Что говорят наши клиенты</h2>
        </div>
        <div className="client_container">
          <Slider {...settings}>
            {dataClient.map((item, index) => (
              <div key={index} className="carousel-item">
                <div className="box">
                  <div className="img-box">
                    <img src={item.img} alt={`Slide ${index + 1}`} />
                  </div>
                  <div className="detail-box">
                    <h5>
                      <span>{item.zagolovok}</span>
                      <hr />
                    </h5>
                    <p>{item.podzagolovok}</p>
                  </div>
                </div>
              </div>
            ))}
          </Slider>
        </div>
      </div>
      <a className="carousel-control-prev" href="#carouselExampleControls" role="button" data-slide="prev">
                <span className="sr-only">Previous</span>
              </a>
              <a className="carousel-control-next" href="#carouselExampleControls" role="button" data-slide="next">
                <span className="sr-only">Next</span>
              
              </a>
    </section>
  );
}
